import React, { useState } from "react";
import './Login.css';
// import ReactDOM from "react-dom";

function Login() {

      // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  // User Login info
  const database = [
    {
      username: "user1",
      password: "pass1"
    },
    {
      username: "user2",
      password: "pass2"
    }
  ];

  const errors = {
    uname: "invalid username",
    pass: "invalid password"
  };

  const handleSubmit = (event) => {
    //Prevent page reload
    event.preventDefault();

    var { uname, pass } = document.forms[0];

    // Find user login info
    const userData = database.find((user) => user.username === uname.value);

    // Compare user info
    if (userData) {
      if (userData.password !== pass.value) {
        // Invalid password
        setErrorMessages({ name: "pass", message: errors.pass });
      } else {
        setIsSubmitted(true);
      }
    } else {
      // Username not found
      setErrorMessages({ name: "uname", message: errors.uname });
    }
  };

  // Generate JSX code for error message
  const renderErrorMessage = (name) =>
    name === errorMessages.name && (
      <div className="error">{errorMessages.message}</div>
    );

  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input__container">
          <label>Username </label>
          <input type="text" className="h-10 border-b-2 border-gray-300 rounded focus:outline-none focus:border-blue-300 mb-2" name="uname" required />
          {renderErrorMessage("uname")}
        </div>
        <div className="input__container">
          <label>Password </label>
          <input type="password" className="h-10 border-b-2 border-gray-300 rounded focus:outline-none focus:border-blue-300" name="pass" required />
          {renderErrorMessage("pass")}
        </div>
        <div className="flex justify-center">
          <input type="submit" className="mt-4 cursor-pointer text-base bg-[#01d28e] text-white px-5 py-4 rounded hover:bg-green-500" />
        </div>
      </form>
    </div>
  );


  return (
    <div className="login flex flex-col items-center justify-center font-mono gap-6 h-screen bg-slate-300">
    <div className="login__container bg-white p-8 w-96 h-96">
      <div className="text-2xl mb-6 text-">Sign In</div>
      {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
    </div>
  </div>
);
}
export default Login
