// import React, { useState, useEffect } from 'react';
import './Movies.css';
const Movies = () => {
const movies =[
    {
        name: 'Hello',
        cast: 'Love',
        language: 'Hindu',
        genre: 'Tony Stack',
        image: 'https://m.media-amazon.com/images/M/MV5BMmUxNGVkZDgtMjE4Zi00NGZhLWI5ZjYtYzk5OTlkMjI5ZDQ5XkEyXkFqcGdeQXVyNTgxODY5ODI@._V1_.jpg'
    },

    {
        name: 'RRR',
        cast: 'Action',
        language: 'Hindu',
        genre: 'Tony Unkown',
        image: 'https://stat1.bollywoodhungama.in/wp-content/uploads/2019/03/RRR-2022-306x393.jpeg'
    }
];
  return (
    <div className='flex gap-4'>
        {movies.map(({name, cast, language, genre, image}) => {
            return <div key={name} className='max-w-xs rounded overflow-hidden shadow-lg'>
    <img src={image} alt="" w-full />
    <div className="px-6 py-2">
        <div className="font-bold text-purple-500 text-xl ">
            {name}
        </div>
        <ul>
            <li>
                <strong> Cast:
                {cast}
                </strong>
            </li>
            <li>
                <strong> Language:
                    {language}
                </strong>
            </li>
            <li>
                <strong> Genre:
                    {genre}
                </strong>
            </li>
        </ul>
    </div>


   </div>

})}
   </div>
  );
};

export default Movies;
