// import React, { useState, useEffect } from 'react';
import './Movies.css';
const Movies = () => {
const movies =[
    {
        name: 'Hello',
        cast: 'Tony Stack',
        language: 'Hindu',
        genre: 'Love',
        image: 'https://m.media-amazon.com/images/M/MV5BMmUxNGVkZDgtMjE4Zi00NGZhLWI5ZjYtYzk5OTlkMjI5ZDQ5XkEyXkFqcGdeQXVyNTgxODY5ODI@._V1_.jpg'
    },

    {
        name: 'RRR',
        cast: 'Hindu',
        language: 'Action',
        genre: 'Tony Unkown',
        image: 'https://nettv4u.com/uploads/04-09-2019/rrr-movie-review.jpg'
    },
    {
        name: 'Thor2',
        cast: 'Mavel',
        language: 'English',
        genre: 'Action',
        image: 'https://kbimages1-a.akamaihd.net/56833984-8c99-4e84-94ae-c9dd0ecb7d67/1200/1200/False/thor-movie-storybook.jpg'
    }
];
  return (
    <div className='flex gap-4'>
        {movies.map(({name, cast, language, genre, image}) => {
            return <div key={name} className='max-w-xs rounded overflow-hidden shadow-lg h-screen'>
    <img src={image} alt="" w-full className='h-4/5' />
    <div className="px-6 py-2">
        <div className="font-bold text-purple-500 text-xl ">
            {name}
        </div>
        <ul>
            <li>
                <strong> Cast:
                {cast}
                </strong>
            </li>
            <li>
                <strong> Language:
                    {language}
                </strong>
            </li>
            <li>
                <strong> Genre:
                    {genre}
                </strong>
            </li>
        </ul>   
        {/* Details Button */}
        <button className='btn py-4 px-4 text-white bg-green-600 rounded mb-2'> View Details</button>


    </div>


   </div>

})}
   </div>
  );
};

export default Movies;
